mapper.update:
UPDATE orders
SET STATUS=3
WHERE ORDER_ID = #{ORDER_ID}

UPDATE orders
SET STATUS=2,
    AMOUNT=#{AMOUNT},
    END_TIME=#{END_TIME}
WHERE ORDER_ID = #{ORDER_ID}

mapper.select:
SELECT MESSAGE_ID, SEND_NAME, RECEIVE_NAME, DETAIL, SEND_TIME, STATUS
FROM message
WHERE SEND_NAME = #{USER_NAME}
   OR RECEIVE_NAME = #{USER_NAME}

mapper.update:
UPDATE message
set STATUS = 1
WHERE MESSAGE_ID = #{MESSAGE_ID}
