mapper.select:
select USER_NAME, PASSWORD, LAST_TIME, TIMES, STATE, REGISTER_TIME, USER_TYPE
from login
where USER_NAME = #{USER_NAME}

mapper.update:
UPDATE login
SET PASSWORD=#{PASSWORD}
WHERE USER_NAME = #{USER_NAME}

UPDATE login
SET LAST_TIME=#{LAST_TIME},
    TIMES=#{TIMES},
    STATE=#{STATE}
WHERE USER_NAME = #{USER_NAME}

UPDATE login
SET STATE=#{STATE}
WHERE USER_NAME = #{USER_NAME}
